mapper.insert:
with tmp(id,item_id) as (select * from (values
(#{item.id},#{item.itemId})
) a) insert into item select tmp.id,b.data_version,b.pac_id,b.org_code from tmp, (select t.id,t.data_version,t.pac_id,t.org_code from item t where t.id >= #{min_id,jdbcType=BIGINT} and t.id <= #{max_id,jdbcType=BIGINT}) b where b.id = tmp.item_id
